const NodeGeocoder = require('./index');

var geocoder = NodeGeocoder({
    provider: 'google',
    language: 'es'
  });

      geocoder.geocode({
        address: "12, rue de l’École de Médecine"
      }).then(function(res) {
        console.log("res: ", res);
      })
      .catch(function(err) {
        console.log("err: ", err);
      });

